    public static List<Spell> getSpellListByLevel(int level) {
        List<Spell> listToReturn = new ArrayList<Spell>(Arrays.asList());
        for (Spell temp : masterSpellList) {
            if (temp.level == level) {
                listToReturn.add(temp);
            }
        }
        return listToReturn;
    }
